    --gradient-warning: linear-gradient(135deg, hsl(45 95% 55%) 0%, hsl(35 90% 50%) 100%);
    --gradient-danger: linear-gradient(135deg, hsl(0 72% 55%) 0%, hsl(350 70% 45%) 100%);
    --gradient-primary: linear-gradient(135deg, hsl(16 85% 60%) 0%, hsl(25 90% 50%) 100%);

    /* Shadows */
    --shadow-glow-safe: 0 0 40px hsl(145 60% 45% / 0.3);
    --shadow-glow-warning: 0 0 40px hsl(45 95% 55% / 0.3);
    --shadow-glow-danger: 0 0 40px hsl(0 72% 55% / 0.3);
    --shadow-glow-primary: 0 0 40px hsl(16 85% 60% / 0.3);
    --shadow-card: 0 10px 40px -10px hsl(0 0% 0% / 0.5);
  }

  * {
    @apply border-border;
  }

  body {
    @apply bg-background text-foreground;
    background: var(--gradient-bg);
    min-height: 100vh;
  }
}

@layer utilities {
  .gradient-safe {
    background: var(--gradient-safe);
  }
  .gradient-warning {
    background: var(--gradient-warning);
  }
  .gradient-danger {
    background: var(--gradient-danger);
  }
  .gradient-primary {
    background: var(--gradient-primary);
  }
  .gradient-card {
    background: var(--gradient-card);
  }
  .shadow-glow-safe {
    box-shadow: var(--shadow-glow-safe);
  }
  .shadow-glow-warning {
    box-shadow: var(--shadow-glow-warning);
  }
  .shadow-glow-danger {
    box-shadow: var(--shadow-glow-danger);
  }
  .shadow-glow-primary {
    box-shadow: var(--shadow-glow-primary);
  }
  .shadow-card {
    box-shadow: var(--shadow-card);
  }
}

/* Animations */
@keyframes pulse-glow {
  0%, 100% {
    box-shadow: 0 0 20px hsl(var(--safe) / 0.4);
  }
  50% {
    box-shadow: 0 0 40px hsl(var(--safe) / 0.6);
  }
}

@keyframes pulse-warning {
  0%, 100% {
    box-shadow: 0 0 20px hsl(var(--warning) / 0.4);
  }
  50% {
    box-shadow: 0 0 40px hsl(var(--warning) / 0.6);
  }
}

@keyframes pulse-danger {
  0%, 100% {
    box-shadow: 0 0 20px hsl(var(--danger) / 0.4);
  }
  50% {
    box-shadow: 0 0 50px hsl(var(--danger) / 0.7);
  }
}

@keyframes heartbeat {
  0%, 100% {
    transform: scale(1);
  }
  14% {
    transform: scale(1.05);
  }
  28% {
    transform: scale(1);
  }
  42% {
    transform: scale(1.05);
  }
  70% {
    transform: scale(1);
  }
}

.animate-pulse-glow {
  animation: pulse-glow 2s ease-in-out infinite;
}

.animate-pulse-warning {
  animation: pulse-warning 1.5s ease-in-out infinite;
}

.animate-pulse-danger {
  animation: pulse-danger 1s ease-in-out infinite;
}

.animate-heartbeat {
  animation: heartbeat 1.5s ease-in-out infinite;
}

